from collections import namedtuple


def has_path(maze, start, destination):
    Node = namedtuple('Node', ('x', 'y'))
    if maze and len(start) == 2 and len(destination) == 2:

        def path_finder_helper(current_node, direction):
            if (current_node and direction in range(4) and (current_node, direction) not in visited_nodes):
                visited_nodes.add((current_node, direction))
                next_node_in_line = Node(current_node.x + shift_with_direction[direction][0],
                                            current_node.y + shift_with_direction[direction][1])
                if next_node_in_line in all_nodes:
                    return path_finder_helper(next_node_in_line, direction)
                else:
                    if current_node == destination_node:
                        return True
                    else:
                        new_direction = (direction + 1) & 3
                        next_nodes_to_visit = list()
                        while new_direction is not direction:
                            new_node = Node(current_node.x + shift_with_direction[new_direction][0],
                                                current_node.y + shift_with_direction[new_direction][1])
                            if new_node in all_nodes:
                                next_nodes_to_visit.append((new_node, new_direction))
                            new_direction = (new_direction + 1) & 3
                        return any([path_finder_helper(*current_node) 
                                    for current_node in next_nodes_to_visit])
            else:
                return False
        
        if start == destination:
                return True
            
        shift_with_direction = ((0, 1), (1, 0), (0, -1), (-1, 0))
        visited_nodes, start_node, destination_node = (set(), Node(start[0], start[1]),
                                                                   Node(destination[0], destination[1]))
        all_nodes = [Node(i, j) for i in range(len(maze)) for j in range(len(maze[0])) if not maze[i][j]]
        
        return any([path_finder_helper(start_node, direction) for direction in range(4)])
        
    return False


